    <section className="men-section">
      <div className="header">
        <h2>Mens Clothing</h2>
        <div className="category-header-line"></div>
      </div>
      <main className="men-body">
        <div className="left">
          <div className="filter-container">
            <header className="filter-header">filter</header>
            <main>
              {filterList.map((filter) => (
                <section className="filter-dropdown">
                <header
                  className="filter-option-header"
                  onClick={() => {
                    setdropdownState(!dropdownState);
                  }}
                >
                  <span>{filter.header}</span>
                  {dropdownState ? (
                    <span >
                      <IoIosArrowDown className="open" />
                    </span>
                  ) : (
                    <span>
                      <IoIosArrowDown className="hide" />
                    </span>
                  )}
                </header>
                <ul
                  className={
                    dropdownState
                      ? "dropdown-options"
                      : "dropdown-options dropdown-options-hide"
                  }
                >
                    {filter.options.map(option => (
                  <li className="option" value={option} onClick={() => selected(option,filter.header)} >{option}</li>      
                    ))}
                 
                </ul>
                
              </section>
              ))}
            </main>
          </div>
        </div>
        <div className="right">
          <div className="products-container">
            {productList.map((product) => (
             
              <Product name={product.name} price={product.price} img={product.img} />
       
            ))}
          </div>
        </div>
      </main>
    </section>
  );
}
