import React, { useEffect, useState } from "react";
import { IoIosArrowDown, IoIosArrowUp } from "react-icons/io";

export function Dropdown() {
    const [dropdownState, setdropdownState] = useState(false);
    const [dropdownOptionState,setdropdownOptionState] = useState({})
    const [selectedItem, setselectedItem] = useState({})
    const {filterChange,options,header} = props
    const selected = (e) =>{
     const item = e.target.value
     setselectedItem({
       header,
       item
     })
     console.log(selectedItem)
    }
    return (
        <section className="filter-dropdown">
        <header
          className="filter-option-header"
          onClick={() => {
            setdropdownState(!dropdownState);
          }}
        >{console.log(dropdownOptionState)}
          <span>{header}</span>
          {dropdownState ? (
            <span >
              <IoIosArrowDown className="open" />
            </span>
          ) : (
            <span>
              <IoIosArrowDown className="hide" />
            </span>
          )}
        </header>
        <ul
          className={
            dropdownState
              ? "dropdown-options"
              : "dropdown-options dropdown-options-hide"
          }
        >
            {options.map(option => (
          <li className="option" value={option} onChange={(e) => filterChange(e)} >{option}</li>      
            ))}
         
        </ul>
        
      </section>
    )
}
