import React, { useState } from "react";
import { IoIosArrowDown, IoIosArrowUp } from "react-icons/io";
import { Dropdown } from "../component/dropDown";
import Product from "../component/product";
import "./men.css";

export function Men(props) {
  const [productList, setproductList] = useState([
  {
    name:'full sleeve tshirt',
    price:500,
  },
  {
    name:'short sleeve tshirt',
    price:600,
  },
  {
    name:' tshirt',
    price:300,
  },
]);
const [filterList,setFilterlist] = useState([
  {
    header:"category",
    options:["Tshirt","Shirt","Jeans"]
  },
  {
    header:"Size",
    options:["XS","S","M","L","XL"]
  },
  {
    header:"Sort By",
    options:["High to Low","Low to high"]
  },
  {
    header:"Gender",
    options:["Male","Female"]
  },
])
  return (
    <section className="men-section">
      <div className="header">
        <h2>Mens Clothing</h2>
        <div className='category-header-line'></div>
      </div>
      <main className="men-body">
        <div className="left">
          <div className="filter-container">
            <header className="filter-header">filter</header>
            <main>
              {filterList.map(filter =>(
                <Dropdown header={filter.header} options={filter.options} />
              ))}
            <Dropdown header='Category' options={["Tshirt","Shirt","Jeans"]} />
            <Dropdown header="Size" options={["XS","S","M","L","XL"]} />
            <Dropdown header="Sleeve" options={["Full Sleeve","Half Sleeve","Sleevless"]} />
            <Dropdown header='Sort By' options={["High to Low","Low to high"]}/>
            <Dropdown header='Sort By' options={["High to Low","Low to high"]}/>
            </main>
          </div>
        </div>
        <div className="right">
          <div className="products-container">
     {productList.map(product => (
       <Product name={product.name} price={product.price} />
     ))}
        
          </div>
        </div>
      </main>
    </section>
  );
}
