import React, { useState } from "react";
import "./App.css";
import logo2 from "./assets/logo2.png";
import {
  FiMail,
  FiFacebook,
  FiTwitter,
  FiLinkedin,
} from "react-icons/fi";
import {
  FaLocationArrow,
  FaPhone,
} from "react-icons/fa";
import useOuterClick from "./utils/useOuterClick";

import Navbar from "./component/navbar"
import AOS from "aos";
import "aos/dist/aos.css";
import { Category } from "./component/category";
import { Trending } from "./component/trending";
AOS.init();

function App() {
  const [mobileNav, setmobileNav] = useState(false);
  const [searchToggler, setsearchToggler] = useState(false);
  const [searchTerm, setsearchTerm] = useState("");

  const innerRef = useOuterClick((ev) => {
    if (mobileNav) {
      setmobileNav(false);
    }
  });
  const searchRef = useOuterClick((ev) => {
    if (searchToggler) {
      setsearchToggler(false);
    }
  });
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(searchTerm);
    setsearchTerm("");
  };

  return (
    <div className="App">
   <Navbar/>
    <Category />

      <Trending/>
      <section className="services-info">
        <div data-aos="fade-right" data-aos-delay="50" className="service">
          <span className="service-icon">
            {" "}
            <svg width="100px" height="100px" fill="#fff">
              {" "}
              <image width="100" height="100" href={free} />
            </svg>
          </span>
          <span className="service-info">Free Delivery for first order</span>
        </div>
        <div data-aos="fade-right" data-aos-delay="100" className="service">
          <span className="service-icon">
            {" "}
            <svg width="100px" height="100px">
              {" "}
              <image width="100" height="100" href={delivery} />
            </svg>{" "}
          </span>
          <span className="service-info">Safe Delivery</span>
        </div>
        <div data-aos="fade-left" data-aos-delay="150" className="service">
          <span className="service-icon">
            <svg width="100px" height="100px">
              {" "}
              <image width="100" height="100" href={secure} />
            </svg>
          </span>
          <span className="service-info">Secure Payment</span>
        </div>
        <div data-aos="fade-left" data-aos-delay="200" className="service">
          <span className="service-icon">
            <svg width="100px" height="100px">
              {" "}
              <image width="100" height="100" href={returnicon} />
            </svg>{" "}
          </span>
          <span className="service-info">10 days return</span>
        </div>
      </section>

      <footer>
        <div className="col col-1-4">
          <header className="footer-header">
            <img style={{ width: "100px" }} src={logo2} alt="" />{" "}
          </header>
          <div className="col-body">
          <div className="location">
            <span className="footer-icon">
              <FaLocationArrow />
            </span>
            <span>Butwal,Kathmandu</span>
          </div>
          <div className="gmail">
            <span className="footer-icon">
              <FiMail />
            </span>
            <span>contact@gmail.com</span>
          </div>
          <div className="phone">
            <span className="footer-icon">
              <FaPhone />
            </span>
            <span>+00-123456789</span>
          </div>
          <div className="social-media">
            <span className="footer-icon">
              <FiFacebook />
            </span>
            <span className="footer-icon">
              <FiTwitter />
            </span>
            <span className="footer-icon">
              <FiLinkedin />
            </span>{" "}
          </div>
          </div>
        </div>
        <div className="col col-2-4">
          <header className="footer-header">Newsletter</header>
          <div className="col-body">
          <div className="newsletter-info">
            Subscribeto our newsletter for latest info and get early discount
          </div>
          <div className="newsletter-input">
            <input  placeholder="Your email address" />
            <button>Subscribe</button>
          </div>
          </div>
        </div>
        <div className="col col-3-4">
        <header className="footer-header">Categories</header>
          <div className="col-body">

          <div className="footer-list">Men</div>
          <div className="footer-list">Women</div>
          <div className="footer-list">Accesories</div>
          <div className="footer-list">Information</div>
          </div>
        </div>

        <div className="col col-4-4">
          <header className="footer-header">Information</header>
          <div className="col-body">
          <div className="footer-list">About Us</div>
          <div className="footer-list">Terms Condition</div>
          <div className="footer-list">Location</div>
          <div className="footer-list">FAQ</div>
          </div>
        </div>

      </footer>
    </div>
  );
}

export default App;
