import React, { useState } from "react";
import "./App.css";
import logo from "./assets/logo.png";
import logo2 from "./assets/logo2.png";
import man from "./assets/man.jpg";
import woman from "./assets/woman.jpg";
import footwear from "./assets/footwear.jpg";
import accesories from "./assets/accesories.jpg";
import {
  FiUser,
  FiSearch,
  FiShoppingCart,
  FiHeart,
  FiPlus, FiMail, FiFacebook, FiTwitter, FiLinkedin
} from "react-icons/fi";
import {FaShuttleVan,FaMoneyBill, FaBlackTie, FaLocationArrow, FaPhone} from 'react-icons/fa'
import useOuterClick from "./utils/useOuterClick";
import Button from "./component/button";
import { Product } from "./component/product";
import free from './assets/services/free.svg';
import delivery from './assets/services/delivery.svg';
import returnicon from './assets/services/return.svg';
import secure from './assets/services/secure.svg';
import AOS from 'aos';
import 'aos/dist/aos.css'; 
AOS.init()

function App() {
  const [mobileNav, setmobileNav] = useState(false);
  const [searchToggler, setsearchToggler] = useState(false);
  const [searchTerm, setsearchTerm] = useState("");

  const innerRef = useOuterClick((ev) => {
    if (mobileNav) {
      setmobileNav(false);
    }
  });
  const searchRef = useOuterClick((ev) => {
    if (searchToggler) {
      setsearchToggler(false);
    }
  });
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(searchTerm);
    setsearchTerm("");
  };

  return (
    <div className="App">
      <div className="navbar">
        <div
          className="hamburger"
          onClick={() => {
            setmobileNav(true);
          }}
        >
          <div className="line1"></div>
          <div className="line2"></div>
          <div className="line3"></div>
        </div>
        <header className="brand-name">
          <img src={logo} alt="" />
        </header>
        <ul className="first-part-nav nav-items">
          <li className=" nav-item">Men</li>
          <li className=" nav-item">Women</li>
          <li className=" nav-item">Accesories</li>
          <li className=" nav-item">Contact</li>
        </ul>
        <ul className="second-part-nav nav-items">
          <li
            className="nav-item bigSearch"
            style={{
              background: "#e8e8e8",
              borderRadius: "6px",
              padding: "3px 11px",
              display: "flex",
              alignItems: "center",
            }}
          >
            <input
              placeholder="search..."
              style={{ background: "#e8e8e8", border: "none", outline: "none" }}
            ></input>
            <FiSearch size={"1.5em"} strokeWidth={'1'} />
          </li>
          {/* phone search */}
          <li
            className="hide-sm-search"
            onClick={() => {
              setsearchToggler(!searchToggler);
            }}
          >
            <span className="responsive-icon">
              {" "}
              <FiSearch size={"1.5em"} style={{ cursor: "pointer" }}   strokeWidth={'1'}/>
            </span>
          </li>
          <form
            className={
              searchToggler
                ? "search-input-container hide-sm-search show-sm-search"
                : "search-input-container hide-sm-search"
            }
            ref={searchRef}
            onSubmit={handleSubmit}
          >
            <input
              placeholder="search..."
              style={{
                background: "#e8e8e8",
                border: "none",
                outline: "none",
              }}
              value={searchTerm}
              onChange={(e) => {
                setsearchTerm(e.target.value);
              }}
            ></input>
          </form>
          <li className="nav-item">
            <span className="responsive-icon">
              <FiUser size={"1.5em"}  strokeWidth={'1'} />
            </span>
          </li>
          <li className="nav-item hideWishList">
            <span className="responsive-icon">
              <FiHeart size={"1.5em"}  strokeWidth={'1'} />
            </span>
            <span className="count">0</span>
          </li>
          <li className="nav-item">
            <span className="responsive-icon">
              <FiShoppingCart size={"1.5em"}  strokeWidth={'1'} />
            </span>
            <span className="count">0</span>
          </li>
        </ul>
      </div>
      {/* burger navbar */}
      <div
        className={
          mobileNav
            ? "mobile-nav-container"
            : "mobile-nav-container hidemobileNav"
        }
      >
        <ul
          className={mobileNav ? "mobile-nav" : "mobile-nav hidemobile"}
          ref={innerRef}
        >
          <header className="mobile-nav-item">
            Welcome Guest{" "}
            <span className="close" style={{ paddingRight: "16px" }}>
              <FiPlus size={"1.5em"} />
            </span>
          </header>
          <li className="mobile-nav-item">Men</li>
          <li className="mobile-nav-item">Women</li>
          <li className="mobile-nav-item">Accesories</li>
          <li className="mobile-nav-item">Contact</li>
          <li className="mobile-nav-item">About Us</li>
        </ul>
      </div>
      <section className="category-section">
        <div className="col-1-3 ">
          <div className="category">
            <img src={man} />
            <Button title="Man" />
          </div>
        </div>
        <div className="col-2-3">
          <div className="category" style={{marginTop:'-16px', marginBottom: "16px" }}>
            <img src={footwear} />
            <Button title="Footwear" />
          </div>
          <div className="category">
            <img src={accesories} />
            <Button title="Accesories" />
          </div>
        </div>
        <div className="col-3-3 ">
          <div className="category">
            <img src={woman} />
            <Button title="Woman" />
          </div>
        </div>
      </section>

      <section className="trending-container">
        <header className='section-header'>
          <div className="header-line"></div>
          <h3>Trending</h3>
          <div className="header-line"></div>
        </header>

        <div className='trending-body'>
          <Product />
          <Product />
          <Product />
          <Product />
          <Product />
          <Product />
          <Product />
          <Product />
        </div>
        <div className="load-more-button-container">
          <button className="load-more-button">Load More</button>
        </div>
      </section>

      <section className='services-info'>
        <div  data-aos="fade-right" data-aos-delay="50"  className="service">
          <span className="service-icon"> <svg width='100px' height='100px' fill='#fff'> <image   width="100" height="100" href={free} /></svg></span>
          <span className="service-info">Free Delivery for first order</span>
        </div>
        <div  data-aos="fade-right"  data-aos-delay="100" className="service">
        <span className="service-icon"> <svg width='100px' height='100px'> <image  width="100" height="100" href={delivery} /></svg> </span>
          <span className="service-info">Safe Delivery</span>
        </div>
        <div  data-aos="fade-left"  data-aos-delay="150" className="service">

          <span className="service-icon"><svg width='100px' height='100px'> <image  width="100" height="100" href={secure} /></svg></span>
          <span className="service-info">Secure Payment</span>
        </div>
        <div  data-aos="fade-left"  data-aos-delay="200" className="service">
          <span className="service-icon"><svg width='100px' height='100px'> <image  width="100" height="100" href={returnicon} /></svg> </span>
          <span className="service-info">10 days return</span>
        </div>
      </section>

      <footer>
        <div className="col-1-4"> 

        <header><img style={{width:"100px"}} src={logo2} /> </header>
        <div className="location"><span className='footer-icon'><FaLocationArrow/></span>Butwal,Kathmandu</div>
        <div className="gmail"><span className='footer-icon'><FiMail/></span>contact@gmail.com</div>
        <div className="phone"><span className='footer-icon'><FaPhone/></span>+00-123456789</div>
        <div className="social-media"><span className='footer-icon'><FiFacebook/><FiTwitter/><FiLinkedin/></span></div>
        </div>
        <div className="col-2-4">
          <header>Categories</header>
          <div className="footer-list">Men</div>
          <div className="footer-list">Women</div>
          <div className="footer-list">Accesories</div>
          <div className="footer-list">Information</div>
        </div>
        <div className="col-3-4">
          <header>Categories</header>
          <div className="footer-list">About Us</div>
          <div className="footer-list">Terms Condition</div>
          <div className="footer-list">Share Location</div>
          <div className="footer-list">FAQ</div>
        </div>
        <div className="col-4-4">
          <header>Newsletter</header>
          <div className='newsletter-info'>Subscribeto our newsletter for latest info and get early discount</div>
        <div className='newletter-input'><input placeholder="Your email address" /><button>Subscribe</button></div>
        </div>
      </footer>
    </div>
  );
}

export default App;
