import React, { useState } from "react";
import Product from "../component/product";
import "./cart.css";
export function Cart(props) {
  const [Cartproduct, setCartproduct] = useState([
    {id:0, name: "tshirt", price: 800, img: "product2.jpg",quantity:0 },
    {id:1, name: "short tshirt", price: 900, img: "product2.jpg",quantity:0 },
    {id:2, name: "long tshirt", price: 100, img: "product2.jpg",quantity:0 },
  ]);
  return (
    <section>
      <header>
        <h2 className="cart-header">Cart</h2>
      </header>
      <main className="cart-body">
          <header className="cart-body-header">
          <h3>Product</h3>
          <h3>Price</h3>
          <h3>Quantity</h3>
          </header>
          {Cartproduct && Cartproduct.map(product => (
              <div className="cart-product">
              <div className="cart-product-display">
                <div className="cart-product-img-container">
                    
                  <img className="cart-product-img" src={`/products/${product.img}`} />
                </div>
              
              </div>
              <div className="cart-product-price">{product.price}</div>
              <div className="cart-product-quantity">
                <span className="increment quantity" onClick={() => {
                  const index = Cartproduct.indexOf(product)
                product.quantity++
                setCartproduct(Cartproduct[index])
               const array = [...Cartproduct]

                  }}>+</span>{product.quantity}
                <span className="decrement quantity">-</span>
              </div>
            </div>
          ))}
       
      </main>
      <div className='price-summary'>
          <header>Price Details</header>
          <div className="prices">
              <div>Price: Rs.80000</div>
              <div>Delivery Charge</div>
              <div>Total:8000000</div>
          </div>
      </div>
    </section>
  );
}
